import React, { useState, useEffect } from 'react';
import { Container, Typography, Box, Button, Card, CardContent, Alert, CircularProgress } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import ApiService from '../../services/api';

const Dashboard = () => {
  const navigate = useNavigate();
  const [dashboardData, setDashboardData] = useState(null);
  const [staffList, setStaffList] = useState([]); // Ensure it's always an array
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Check zauthentication first
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login', { replace: true });
      return;
    }

    loadDashboardData();
  }, [navigate]);

  const loadDashboardData = async () => {
    try {
      console.log('Loading dashboard data...');

      // Load dashboard data with proper error handling
      let dashboard = { company_name: 'Your Company' };
      let staff = [];

      try {
        dashboard = await ApiService.getDashboard();
        console.log('Dashboard API response:', dashboard);
      } catch (err) {
        console.warn('Dashboard endpoint failed:', err);
      }

      try {
        const staffResponse = await ApiService.getStaffList();
        console.log('Staff API response:', staffResponse);

        // Handle different response formats
        if (Array.isArray(staffResponse)) {
          staff = staffResponse;
        } else if (staffResponse && Array.isArray(staffResponse.data)) {
          staff = staffResponse.data;
        } else if (staffResponse && Array.isArray(staffResponse.staff)) {
          staff = staffResponse.staff;
        } else {
          console.warn('Staff response is not an array:', staffResponse);
          staff = [];
        }
      } catch (err) {
        console.warn('Staff list endpoint failed:', err);
        staff = [];
      }

      setDashboardData(dashboard);
      setStaffList(staff);
      console.log('Final state:', { dashboard, staff: staff.length });

    } catch (error) {
      console.error('Dashboard error:', error);
      setError(error.message || 'Failed to load dashboard data.');

      // Ensure staffList is still an array even on error
      setStaffList([]);

      // If authentication fails, redirect to login
      if (error.message.includes('401') || error.message.includes('Session expired')) {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        navigate('/login', { replace: true });
      }
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = async () => {
    try {
      await ApiService.logout();
    } catch (error) {
      console.error('Logout error:', error);
    } finally {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      navigate('/login', { replace: true });
    }
  };

  if (loading) {
    return (
      <Container>
        <Box sx={{ mt: 4, textAlign: 'center' }}>
          <CircularProgress />
          <Typography sx={{ mt: 2 }}>Loading Dashboard...</Typography>
        </Box>
      </Container>
    );
  }

  // Ensure staffList is always an array
  const safeStaffList = Array.isArray(staffList) ? staffList : [];

  return (
    <Container maxWidth="lg">
      <Box sx={{ mt: 4 }}>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 4 }}>
          <Typography variant="h4">Dashboard</Typography>
          <Button variant="outlined" onClick={handleLogout}>
            Logout
          </Button>
        </Box>

        {error && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}

        <Box sx={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: 3, mb: 4 }}>
          <Card>
            <CardContent>
              <Typography variant="h6" color="textSecondary">
                Total Staff
              </Typography>
              <Typography variant="h3" color="primary">
                {safeStaffList.length}
              </Typography>
            </CardContent>
          </Card>

          <Card>
            <CardContent>
              <Typography variant="h6" color="textSecondary">
                Company
              </Typography>
              <Typography variant="h6">
                {dashboardData?.company_name || 'Your Company'}
              </Typography>
            </CardContent>
          </Card>
        </Box>

        <Box sx={{ display: 'flex', gap: 2, mb: 4 }}>
          <Button
            variant="contained"
            onClick={() => navigate('/add-staff')}
            size="large"
          >
            Add New Staff
          </Button>
        </Box>

        <Typography variant="h5" gutterBottom sx={{ mt: 4 }}>
          Staff Members
        </Typography>

        {safeStaffList.length === 0 ? (
          <Card>
            <CardContent>
              <Typography variant="body1" textAlign="center" color="textSecondary">
                No staff members added yet. Click "Add New Staff" to get started.
              </Typography>
            </CardContent>
          </Card>
        ) : (
          <Box sx={{ display: 'grid', gap: 2 }}>
            {safeStaffList.map((staff, index) => (
              <Card key={staff.id || index}>
                <CardContent>
                  <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                    <Box>
                      <Typography variant="h6">
                        {staff.firstname} {staff.lastname}
                      </Typography>
                      <Typography color="textSecondary">
                        {staff.employment_status}
                      </Typography>
                      <Typography variant="body2">
                        NI: {staff.national_insurance_number}
                      </Typography>
                      <Typography variant="body2">
                        Phone: {staff.telephone_number}
                      </Typography>
                    </Box>
                    <Box>
                      <Button
                        variant="outlined"
                        size="small"
                        onClick={() => navigate(`/edit-staff/${staff.id}`)}
                        sx={{ mr: 1 }}
                      >
                        Edit
                      </Button>
                    </Box>
                  </Box>
                </CardContent>
              </Card>
            ))}
          </Box>
        )}
      </Box>
    </Container>
  );
};

export default Dashboard;
